import React,{ Component } from 'react';
import './App.css';
import axios from "axios"
import Home from "./Home"
import Show from "./components/Show"
import Login from "./components/Login"
import { getToken, setToken, logout} from './services/auth'
import {
  BrowserRouter as Router,
  Route,
  Link
} from 'react-router-dom'

//header file for auth
let header = {
  headers :{
    "Content-Type" : "application/json",
    "Authorization" : `Bearer ${getToken()}`
  }
}


export default class App extends Component {
  state={
    id:[],
    title:[],
    url:[],
    train_score:[],
    test_score:[],
    user : "",
    errorMsg : '',
    isAuthenticated : false,
    hasError : false
  }

  changeHandler = (e) => {
    //Log every key value and save to state from form
    let data = {...this.state}
    data[e.target.name] = e.target.value

    this.setState(data)
  }

  submitHandler = (e) => {   
    axios.post('/',{ name : this.state.gamename}, header)
      .then( response => {

          let data = {...this.state}
          data.games.push(response.data.game)

          this.setState(data)
      })
      .catch()
  }

  logout = () =>{
    logout()
    let data = {...this.state}
    //reset everything on logout
    data.isAuthenticated = false
    data.user = ""
    data.email = ""
    data.password = ""
    data.games = []

    this.setState(data)
  }

  getData=()=>{
    if (this.state.id[0])return null
    axios.get('main/',header)
    .then(data=>{
      let newState={...this.state}
      data.data.datum.forEach((item)=>{
        newState.id.push(item._id)
        newState.title.push(item.title)
        newState.train_score.push(item.train_acc)
        newState.test_score.push(item.test_acc)
        newState.url.push('http://placehold.it/300x300')
      })

      this.setState(newState)
    })
  }

  render() {
    const logged=(this.state.isAuthenticated)?<div className='text-white logout' onClick={this.logout} >Logout</div>:<Link to='/login' className='text-white'>Login</Link>;
    return (
      <div>
      <Router>
      <nav className='bg-dark p-1 d-flex flex-row justify-content-between'>
        <Link to='/' className='text-white'>Home</Link>
        {logged}
      </nav>
        <Route exact path='/' render={()=><Home state={this.state} getData={this.getData} />} />
        <Route path='/login' render={()=> <Login isAuth={false} />}/>
        <Route path='/show/:id' component={Show} />
      </Router>
      </div>
    )
  }
}
